code_templates.api_template:
import { request } from '@/utils/request';

// {API_NAME} API接口
export interface {ModelName} {
  id: string;
  name: string;
  // 其他字段
}

// 获取列表
export function get{ModelName}List(params?: any) {
  return request<{ModelName}[]>({
    url: '/{api_path}',
    method: 'GET',
    params
  });
}

// 获取详情
export function get{ModelName}Detail(id: string) {
  return request<{ModelName}>({
    url: `/{api_path}/${id}`,
    method: 'GET'
  });
}

// 创建
export function create{ModelName}(data: Partial<{ModelName}>) {
  return request<{ModelName}>({
    url: '/{api_path}',
    method: 'POST',
    data
  });
}

// 更新
export function update{ModelName}(id: string, data: Partial<{ModelName}>) {
  return request<{ModelName}>({
    url: `/{api_path}/${id}`,
    method: 'PUT',
    data
  });
}

// 删除
export function delete{ModelName}(id: string) {
  return request({
    url: `/{api_path}/${id}`,
    method: 'DELETE'
  });
}

code_templates.store_template:
import { defineStore } from 'pinia';

export interface {StoreName}State {
  // 状态定义
  loading: boolean;
  data: any[];
  error: string | null;
}

export const use{StoreName}Store = defineStore('{storeName}', {
  state: (): {StoreName}State => ({
    loading: false,
    data: [],
    error: null
  }),

  getters: {
    // 计算属性
    hasData: (state) => state.data.length > 0
  },

  actions: {
    // 异步操作
    async fetchData() {
      this.loading = true;
      this.error = null;

      try {
        // API调用
        const response = await api.getData();
        this.data = response.data;
      } catch (error) {
        this.error = error.message;
        console.error('获取数据失败:', error);
      } finally {
        this.loading = false;
      }
    },

    // 同步操作
    clearData() {
      this.data = [];
      this.error = null;
    }
  }
});
